import "../styles/delivery-jobs.css";
import { useState } from "react";

const DeliveryHistory = ({ orderHistory }) => {
  const [deliveryTimes, setDeliveryTimes] = useState({});
  const [markedOrders, setMarkedOrders] = useState({});

  const handleClick = (orderId) => {
    if (!markedOrders.includes(orderId)) {
      const currentTime = new Date().toLocaleString();
      setDeliveryTimes((prevTimes) => ({
        ...prevTimes,
        [orderId]: currentTime,
      }));
      setMarkedOrders((prevOrders) => [...prevOrders, orderId]);
    }
  };

  const history = orderHistory.map((order, index) => {
    return (
      <div className="history-listing" key={index}>
        <p>
          <span className="bold-text">Order Ref.</span> {order.id}
        </p>
        <p className="grid-start-1">
          <span className="bold-text">Postcode: </span>
          {order.postcode}
        </p>
        <p>
          <span className="bold-text">Address:</span> {order.address}
        </p>
        <button onClick={() => handleClick(order.id)} disabled={markedOrders.includes(order.id)}>Mark as Delivered</button>
      </div>
    );
  });

  return (
    <>
      <h3 className="delivery-h3">Delivery History</h3>
      <div className="history">
        <div className="">{history}</div>
      </div>
    </>
  );
};

export default DeliveryHistory;
