import React from "react";
import { useNavigate } from "react-router-dom";

const DeliveryJob = ({ job, updateBtn, btnMessage }) => {
  const navigate = useNavigate();

  const handleUpdateButton = () => {
    updateBtn(job.id);
    navigate("/my-deliveries");
  };

  // This function will open the address in a new tab using Google Maps
  const openInMaps = () => {
    const mapUrl = `https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(job.address)}`;
    window.open(mapUrl, "_blank");
  };

  return (
    <section className="job">
      <div className="job-details">
        <p>Postcode: {job.postcode}</p>
        <p>Address: {job.address}</p>

        <p>Customer Name: {job.customerName}</p>
        <p>Volume: {job.volume}</p>

      </div>
      {/* Map section */}
      <div className="map-container">
        <iframe
          title="Map"
          src={`https://maps.google.com/maps?q=${encodeURIComponent(job.address)}&z=15&output=embed`}
          allowFullScreen
        ></iframe>
      </div>
      <div>
        <button onClick={handleUpdateButton}>{btnMessage}</button>
      </div>
    </section>
  );
};

export default DeliveryJob;
